import React,{useState} from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
import "./Expenses.css";

const Expenses = (props) => {
  const [filterdYear, setfilterdYear] = useState("2022"); 
  const filterYearChangeHandler = (year) =>{
    setfilterdYear(year);
  };
  return (
    <Card className="expenses">
      <ExpensesFilter selected={filterdYear} filterYearChange={filterYearChangeHandler} />
      {props.items.filter(item => item.date = filterdYear).map((item) => (
        <ExpenseItem key={item.id} title={item.title} amount={item.amount} date={item.date} />
      ))}
    </Card>
  );
}

export default Expenses;
